import React from "react";
import { useEffect, useContext } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import Bookcontext from '../BookContext/context';
import axios from "axios";
import Book from "./Book";

function IndividualBook(props) {
    const nav = useNavigate();
    const location = useLocation();
    const { ID } = location.state;
    const context = useContext(Bookcontext);
    const { book, particularBook } = context;
    useEffect(() => {
        particularBook(ID);
    }, []);

    async function Delete() {
        await axios
            .delete("http://localhost:5500/deleteBook/" + ID, {
                headers: { "Authorization": `Bearer ${localStorage.getItem('token')}` }
            })
            .then(res => console.log(res.data))

        alert("Deleted Successfully");
        nav("/");
    }

    console.log(book);

    return (
        <main class="container">

            {/* Image */}
            <div class="left-column">
                <img data-image="black" src={book.Image} alt="" />
            </div>

            <div class="right-column">

                <div class="product-description">
                    <span>{book.Name}</span>
                    <h1>{book.Author}</h1>
                    <p>Description</p>
                </div>


                <div class="product-configuration">

                    <div class="cable-config">
                        <span>Tags</span>

                        <div class="cable-choose">
                        {/* {.map(item=><button>{item}</button>)} */}
                            
                        </div>

                        <a href="#">How to configurate your headphones</a>
                    </div>
                </div>


                <div class="product-price">
                    <span>148$</span>
                    <a href="#" class="cart-btn">Add to cart</a>
                </div>
            </div>
        </main>
    );
}

export default IndividualBook;

// {localStorage.getItem("token") && <button onClick={Delete}>Delete Book</button>}
