import React, { useState } from "react";
import "../non-components/BookForm.css"
import axios from "axios";

const details = {
    bookName: "",
    authorName: "",
    bookImage: "",
    bookRating: 0,
    bookTags: [],
    copies: 0
};

function BookForm() {
    const [contact, setContact] = useState({
        name: "",
        author: "",
        image: "",
        rating: "",
        tags: [],
        countCopy: ""
    });

    function update(event) {
        const { name, value } = event.target;
        // if(name === "rating")
        // {
        //   setContact((prevValue) =>
        //   {
        //     return {
        //       ...prevValue,
        //       rating: (event.target.validity.valid ? event.target.value : value)
        //     }
        //   })
        // }
        if (name === "tags") {
            const val = value.split(",");
            setContact((prevValue) => {
                return {
                    ...prevValue,
                    tags: val
                };
            });
        }

        if (name !== "tags") {
            setContact((prevValue) => {
                return {
                    ...prevValue,
                    [name]: value
                };
            });
        }
    }

    function onSubmit(event) {

        event.preventDefault();
        details.bookName = contact.name;
        details.authorName = contact.author;
        details.bookImage = contact.image;
        details.bookRating = contact.rating;
        details.bookTags = contact.tags;
        details.copies = contact.countCopy;

        setContact(() => {
            return {
                Name: "",
                author: "",
                image: "",
                rating: "",
                tags: [],
                countCopy: ""
            };
        });

        axios
            .post("http://localhost:5500/post/", details)
            .then(res => console.log(res.data))

    }

    return (
        <div className="container">
            <h1>Add New Book</h1>
            <p>{contact.email}</p>
            <form onSubmit={onSubmit}>
                <input
                    onChange={update}
                    name="name"
                    placeholder="Book Name"
                    value={contact.N}
                />
                <input
                    onChange={update}
                    name="author"
                    placeholder="Book Author"
                    value={contact.author}
                />
                <input
                    onChange={update}
                    name="image"
                    placeholder="Book Image URL"
                    value={contact.image}
                />
                <input
                    onChange={update}
                    type="text"
                    pattern="[0-5]*"
                    name="rating"
                    placeholder="Book Rating (0-5)"
                    value={contact.rating}
                />
                <input
                    onChange={update}
                    name="tags"
                    placeholder="Tags seperated by ,(comma)"
                    value={contact.tags}
                />
                <input
                    onChange={update}
                    name="countCopy"
                    placeholder="Number of Copies"
                    value={contact.countCopy}
                />
                <button type="submit">Submit</button>
            </form>
        </div>
    );
}

export default BookForm;

export { details };
