import {
    BrowserRouter,
    Switch,
    Route,
    Routes,
    Redirect, useNavigate, Link
} from "react-router-dom";
import React from "react";
import BookListRender from "./BookListRender";
import IndividualBook from "./IndividualBook";

function Book(props) {

    const navigate = useNavigate();

    

    return (
        <div>
            <Link to="/IndividualBook" state={{ID:props.ID}}>
            <button className="book" onClick={navigateBook}>
                <div >
                    <img alt="NA" />
                    <h1>{props.name}</h1>
                    <p>{props.author}</p>
                </div>
            </button>  
            </Link>          
        </div>
    );
}

export default Book;
