import React, { useEffect, useState } from "react";
import Header from "./Header";
import Footer from "./Footer";
import Book from "./Book";

function App() {

    const [book,setBooks] = useState([
        {
            name:"",
            author:"",
            img:""
        }
    ])


    useEffect(()=> {
        fetch("http://localhost:5500/post/")
        .then((res)=>res.json())
        .then((jsonRes)=>setBooks(jsonRes))
    },[]);

    useEffect(()=>{
        console.log(book);
    },[book]);

    return (
        <div>
            <Header />
            {book.map(item => (
                <Book name={item.Name} author={item.Author} image={item.img} />
            ))}
            <Footer />

        </div>
    );
}

export default App;
