import React, { useContext, useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import Bookcontext from "../BookContext/context";
import Book from "./Book";


function BookListRender() {
    const nav = useNavigate();

    const context = useContext(Bookcontext);
    const { book, allBooks } = context;
    useEffect(() => {
        allBooks();
    }, []);

    const Logout = () => {
        if (localStorage.getItem('token')) {
            localStorage.setItem('token', "");
            alert("Logged out sucessfully!");
            window.location.reload();
        }
        else {
            alert("Need to Login first!");
        }
    }

    const Check = () => {
        if (localStorage.getItem('token')) {
            nav("/newBook")
        }
        else {
            alert("Need to Login first!");
            nav("/");
        }
    }

    


    return (
        <div>
        <div className="homeButtons">
            {localStorage.getItem("token") && <button onClick={Logout} type="button" class="btn btn-secondary btn-lg homeButton">logout</button>}
            {localStorage.getItem("token") && <button onClick={Check} type="button" class="btn btn-secondary btn-lg homeButton">Book Form</button>}
            {!localStorage.getItem("token") && <button type="button" class="btn btn-secondary btn-lg homeButton"> 
                <Link to="/login">
                    User Login
                </Link>
            </button>}
            {localStorage.getItem("token") && <button type="button" class="btn btn-secondary btn-lg home-buttons">Logged In</button>}
            </div>
            {book.map(item => (
                <Book name={item.Name} author={item.Author} id={item._id} image={item.Image} />
            ))}
        </div>
    );
}

export default BookListRender;
